import React, { Component } from 'react';
import QuakesList from '../QuakesList';

class MainContainer extends Component {
    constructor(){
        super();

        this.state={
            quakes:[],
            loading: true
        }
    }

    getQuakes = async () => {
        try {
            let url = 'https://earthquake.usgs.gov/fdsnws/event/1/application.json?format=geojson';

            const quakes = await fetch(url);
            if(!quakes.ok){
                throw Error()
            }




        } catch (err) {
            console.log(err,'error in catch block');
            return err;        
        }
    }




    render(){
        return(
            <div>
                {this.state.loading ? <span>Data Is Loading ...</span> : <QuakesList quakes={this.state.quakes}/>}
            </div>

        )
    }

}

export default MainContainer;
