import React from 'react';

const QuakesList = (props) => {

    const quakesList = props.quakes.features.map((quake, i) => {
        let currentTime = new Date().getTime();
        let timeInSecond = quake.properties.time;
        // timeInSecond = Math.round(timeInSecond / 1000) ;
        let diffrenceTimeInSecond = Math.floor((currentTime - timeInSecond) / 1000);
        let hoursAgo = Math.floor(diffrenceTimeInSecond / 24);

        return <p>{quake.properties.title} / {hoursAgo} hours a </p>
    });

    return(
        <div id="info">
            {quakesList}
        </div>
    )


}

export default QuakesList;
