import React, { Component } from 'react';
import {Map, GoogleApiWrapper, Marker, InfoWindow} from 'google-maps-react';

const mapStyles = {
    height: '100%',
    width:'40%',
    position:"relative"
}

export class MapContainer extends Component {

    constructor(){
        super();
        this.state = {
            showingInfoWindow: false,
            activeMarker: {},
            selectedPlace: {}
        }
    }


    onMarkerClick = (props, marker, e) => {
        this.setState({
            activeMarker: marker,
            selectedPlace: props
        })
    }




    render(){

        const {google} = this.props;

        const quakesList = this.props.quakes.features.map((quake, i) => {
            return(
                <Marker key={i} 
                position={{lat:quake.geometry.coordinates[1], lng:quake.geometry.coordinates[0]}}
                icon={{url: "../../public/images/earthquake.png", anchor: new google.maps.Point(32,32),
                scaledSize: new google.maps.Size(32,32)}}
                onClick={this.onMarkerClick}
                />
            )
        });

        return(

            <Map
                google={this.props.google}
                zoom={8}
                style={mapStyles}
                initialCenter={{
                    lat: 30.2682, 
                    lng: -97.74295
                }}
            >
            {/* <Marker

                onClick={this.onMarkerClick}
                icon={{url:'/public/images/earthquake.png'}}
                // name={'test'}
            /> */}
            {quakesList}
            </Map>
        )
    }
}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyBHLett8djBo62dDXj0EjCimF8Rd6E8cxg'
})(MapContainer);
